        return new Source("{% extends 'base.html.twig' %}

{% block title %}User index{% endblock %}

{% block body %}
<div class=\"d-flex justify-content-around mt-3 mb-3\">
    <h1 class=\"text-info\"><b>Users list</b></h1>
</div>

    <table class=\"table table-striped table-dark text-light\">
        <thead>
            <tr>
                <th class=\"text-info\">Id</th>
                <th class=\"text-info\">Email</th>
                <th class=\"text-info\">Roles</th>
                <th class=\"text-info\">Password</th>
                <th class=\"text-info\">IsVerified</th>
                <th class=\"text-info\">actions</th>
            </tr>
        </thead>
        <tbody>
        {% for user in users %}
            <tr>
                <td>{{ user.id }}</td>
                <td>{{ user.email }}</td>
                <td>{{ user.roles ? user.roles|json_encode : '' }}</td>
                <td>{{ user.password }}</td>
                <td>{{ user.isVerified ? 'Yes' : 'No' }}</td>
                <td>
                    <a class=\"text-light\" href=\"{{ path('app_user_show', {'id': user.id}) }}\"><button class=\"btn btn-info\">Show</a></button>
                    <a class=\"text-light\" href=\"{{ path('app_user_edit', {'id': user.id}) }}\"><button class=\"btn btn-info\">Edit</a></button>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"6\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>
{% endblock %}
", "user/index.html.twig", "/home/alexis/Documents/DIW22091/Exos/Symfony/Record3/templates/user/index.html.twig");
